package jcom.codegnan.javapractice;

public class ArithemeticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10,b = 20;
		a = a + b;
		System.out.println(a);
		a = a - b;
		System.out.println(a);
		a = a * b;
		System.out.println(a);
		a = a / b;
		System.out.println(a);
		
		a = a % b;
		System.out.println(a);


	}

}

